import React, { Component } from 'react'

import employees from "./employees.json";

import EmployeeCard from '../EmployeeCard'

class VisibleEmployees extends Component {
  constructor() {
    super()
    this.state = {
      employees: [],
      filteredEmployees: []
    }
  }

  removeEmployee = id => {
    // Filter this.state.employees for employees with an id not equal to the id being removed
    const employees = this.state.employees.filter(employee => employee.id !== id);
    // Set this.state.employees equal to the new employees array
    this.setState(employees=employees);
  };


  componentWillMount() {
    this.setState({
      employees,
      filteredEmployees: employees
    })
  }

  filterEmployees = (employeeFilter) => {
    let filteredEmployees = this.state.employees
    filteredEmployees = filteredEmployees.filter((employee) => {
      let employeeName = employee.name.toLowerCase()
      return employeeName.indexOf(
        employeeFilter.toLowerCase()) !== -1
    })
    this.setState({
      filteredEmployees
    })
  }

  render() {
    return (

    <div>
      {this.state.filteredEmployees.map(employee => (
          <EmployeeCard
          removeEmployee={this.removeEmployee}
          id={employee.id}
          key={employee.id}
          name={employee.name}
          image={employee.image}
          occupation={employee.occupation}
          location={employee.location}
          match={this.props.match}
          onChange={this.filteredEmployees}
          />
        ))}
    </div>
    )
  }
}

export default VisibleEmployees

