import React, { Component } from 'react'

import employees from "./employees.json";
import SearchForm from "./SearchForm";
import ResultList from "./ResultList";

// import EmployeeCard from '../EmployeeCard'

class EmployeesResultContainer extends Component {
    state = {
      search: "",
      results: [],
      employees: employees
    }


    searchEmployees = function (query) {

      let searchEmployees = this.state.employees.filter(employee => {
        console.log(employee.name)
        return employee.name == query;  /* can use just - (return user.online),
                                         put for your  reference. */
      })
    };


    removeEmployee = id => {
      // Filter this.state.employees for employees with an id not equal to the id being removed
      const employees = this.state.employees.filter( employee => employee.id !== id);
      // Set this.state.employees equal to the new employees array
      this.setState(this.state.employees);
    };


    handleInputChange = event => {
      const name = event.target.name;
      const value = event.target.value;
      this.setState({
        [name]: value
      });
    };

    // When the form is submitted, search the Employees JSON for `this.state.search`
    handleFormSubmit = event => {
      event.preventDefault();
      this.searchEmployees(this.state.search);
    };


  render() {
    return (

    <div>
        <SearchForm
          search={this.state.search}
          handleFormSubmit={this.handleFormSubmit}
          handleInputChange={this.handleInputChange}
        />
        <ResultList results={this.state.results} removeEmployee={this.removeEmployee}/>
    </div>
    )
  }
}

export default EmployeesResultContainer

